import React, { useState, useEffect } from 'react';
import '../../styles/main.css';

const ShipForm = ({ onSave, editingShip }) => {
    const [formData, setFormData] = useState({ name: '', imo: '', flag: '', status: '' });

    useEffect(() => {
        if (editingShip) {
            setFormData(editingShip);
        } else {
            setFormData({ name: '', imo: '', flag: '', status: '' });
        }
    }, [editingShip]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(formData);
        setFormData({ name: '', imo: '', flag: '', status: '' });
    };

    return (
        <div className="add-ship-container">
            <h2>{editingShip ? 'Edit Ship' : 'Add Ship'}</h2>
            <form onSubmit={handleSubmit}>
                <input
                    name="name"
                    placeholder="Name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
                <input
                    name="imo"
                    placeholder="IMO Number"
                    value={formData.imo}
                    onChange={handleChange}
                    required
                />
                <input
                    name="flag"
                    placeholder="Flag"
                    value={formData.flag}
                    onChange={handleChange}
                    required
                />
                <input
                    name="status"
                    placeholder="Status"
                    value={formData.status}
                    onChange={handleChange}
                    required
                />
                <button type="submit" className="add-button">
                    {editingShip ? 'Update' : 'Add'}
                </button>
            </form>
        </div>
    );
};

export default ShipForm;
